import * as api from "../api";

// Actions Creators
export const getUsers = () => async(dispatch)  => {
    try {
        const { data } = await api.getUsers();
        dispatch({ type: "FETCH_USERS", payload: data })
    } catch (error) {
        console.log(error.message);
    }
}

export const updateFriends = (friendData) => async(dispatch) => {
    try {
        const { data } = await api.newFriends(friendData);

        dispatch({ type: "ADD_FRIENDS", payload: data})
    } catch (error) {
        console.log(error.message);
    }
}

export const getCurrentFriends = (currentFriends) => async(dispatch) => {
    try {
        console.log('here1');
        const { data } = await api.currentFriends(currentFriends);
        console.log(data);
        dispatch({ type: "GET_FRIENDS", payload: data })
    } catch (error) {
        console.log(error.message);
    }
}
