const reducer = (friends = [], action) => {
    switch(action.type) {
        case "FETCH_USERS":
            return action.payload;
        case "ADD_FRIENDS":
            localStorage.setItem('profile', JSON.stringify({ ...action?.payload }))
            return action.payload;
        case "GET_FRIENDS":
        default:
            return friends;
    }
}

export default reducer;
