import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUsers } from "../../actions/friends";
import { DataGrid } from '@material-ui/data-grid';
import { Button } from "@material-ui/core";

const columns = [
  { field: 'firstName', headerName: 'First name', width: 130 },
  { field: 'lastName', headerName: 'Last name', width: 130 },
];

const AddFriends = () => {
    const dispatch = useDispatch();
    const users = useSelector((state) => state.friends);
    const [newFriends, setNewFriends] = useState([]);
    console.log(users);

    console.log("New Friends", newFriends);

    const rows = []

    users.forEach(user => {
        const id = user._id;
        const firstName = user.firstName;
        const lastName = user.lastName;
        const row = { id: id, firstName, lastName };
        rows.push(row);
    });

    useEffect(() => {
        dispatch(getUsers())
    }, [dispatch])
    return (
        <div style={{ height: 400, width: '100%' }}>
            <DataGrid 
                rows={rows} 
                columns={columns} 
                pageSize={5} 
                checkboxSelection 
                onSelectionModelChange={(newSelection) => {
                    setNewFriends(newSelection.selectionModel);
                }}
                selectionModel={newFriends}
                    {...rows}
            />
            <Button color="primary" variant="contained" onClick={addFriends}>Add Friends</Button>
        </div>
    )
}

export default AddFriends;
