import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUsers, updateFriends } from "../../actions/friends";
import { DataGrid } from '@material-ui/data-grid';
import { Button } from "@material-ui/core";

const columns = [
  { field: 'firstName', headerName: 'First Name', width: 130 },
  { field: 'lastName', headerName: 'Last Name', width: 130 },
  { field: 'state', headerName: 'State', width: 130 },
];

const AddFriends = () => {
    const dispatch = useDispatch();
    const users = useSelector((state) => state.friends);
    const currentUser = JSON.parse(localStorage.getItem('profile'));
    const [newFriends, setNewFriends] = useState([]);
    const currentFriends = currentUser?.public?.friends;

    const addFriends = () => {
        console.log("current id", currentUser._id);
        newFriends.push(currentUser._id);
        dispatch(updateFriends(newFriends));
    }

    const rows = []

    users.forEach(user => {
        console.log("user", user);
        const id = user._id;
        if (!currentFriends || !currentFriends.includes(id)) {
            const firstName = user.public.firstName;
            const lastName = user.public.lastName;
            const state = user.public.state;
            if (!state) {
                const row = { id: id, firstName, lastName, state:"NA" };
                rows.push(row);
            }
            else {
                const row = { id: id, firstName, lastName, state };
                rows.push(row);
            }  
        }
    });

    useEffect(() => {
        dispatch(getUsers())
    }, [dispatch])
    return (
        <div style={{ height: 400, width: '100%' }}>
            <DataGrid 
                rows={rows} 
                columns={columns} 
                pageSize={5} 
                checkboxSelection 
                onSelectionModelChange={(newSelection) => {
                    setNewFriends(newSelection.selectionModel);
                }}
                selectionModel={newFriends}
                    {...rows}
            />
            <Button color="primary" variant="contained" onClick={addFriends}>Add Friends</Button>
        </div>
    )
}

export default AddFriends;
