import React, { useState, useEffect } from "react";
import { Container, Grow, Grid } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";

import { getPosts } from "../../actions/posts";
import { getCurrentFriends } from "../../actions/friends";
import Posts from "../Posts/Posts";
import Mileage from "../Friends/Mileage";

export const Home = () => {
    const [currentId, setCurrentId] = useState(null);
    const [friends, setCurrentFriends] = useState([]);
    const dispatch = useDispatch();
    const user = JSON.parse(localStorage.getItem('profile'));

    const currentFriends = user?.public?.friends;
    console.log(user);

    useEffect(() => {
        dispatch(getPosts());
    }, [currentId, dispatch]);

    useEffect(() => {
        dispatch(getCurrentFriends(currentFriends));
    }, [friends, dispatch]);

    return (
        <Grow in>
            <Container>
                <Grid container justify="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={7}>
                        <Posts setCurrentId={setCurrentId} currentId={currentId}/>
                    </Grid>
                    {/* <Grid item xs={12} sm={4}>
                        <Form currentId={currentId} setCurrentId={setCurrentId} />
                    </Grid> */}
                    <Grid item xs={12} sm={4}>
                        <Mileage currentId={currentId} friends={friends} />
                    </Grid>
                </Grid>
            </Container>
        </Grow>
    )
}

export default Home;
