import React, { useState, useEffect } from "react";
import { AppBar, Avatar, Button, ThemeProvider, Toolbar, Typography } from "@material-ui/core"
import { Link, useHistory, useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";

import { useStyles, theme } from "./styles";
import memories from "../../images/memories.png";

const Navbar = () => {
    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation();

    const logout = () => {
        dispatch({ type: "LOGOUT" });

        history.push("/");

        setUser(null);
    }

    useEffect(() => {
        const token = user?.token;

        // JWT ..

        setUser(JSON.parse(localStorage.getItem("profile")));
    }, [location]);

    return (
    <ThemeProvider theme={theme}>
        <AppBar className={classes.appBar} position="static" color="inherit">
            <div className={classes.brandContainer}>
                <img className={classes.image} src={memories} alt="memories" height="60" />
                <Typography component={Link} to="/" className={classes.heading} variant="h2" align="center">W2W</Typography>
                <img className={classes.image} src={memories} alt="memories" height="60" />
            </div>
            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.purple} alt={user.result.name} src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
                        <Typography className={classes.userName} variant="h6">{user.result.name}</Typography>
                        <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
                    </div>
                ) : (
                    <Button component={Link} to="/auth" variant="contained" color={c_theme.palette.primary}>Sign In</Button>
                )}
            </Toolbar>
        </AppBar>
    </ThemeProvider>
    );
}

export default Navbar;
