const reducer = (friends = [], action) => {
    switch(action.type) {
        case "FETCH_ALL":
            return action.payload;
        case "ADD_FRIENDS":
            return { ...state, userData: action.data, loading: false, errors: null }
        default:
            return friends;
    }
}

export default reducer;
