import React from 'react';
import { Paper, Button, Typography } from "@material-ui/core";
import RadioComp from "../FormComponents/RadioComp";
import BasicProfile from "../FormComponents/BasicInfo";
import CheckBoxInfo from "../FormComponents/CheckboxInfo";
import Address from "../FormComponents/AddressInfo";
import MultipleSelect from "../FormComponents/MultipleSelect";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { updateProfile } from "../../actions/auth";


const Profile = () => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [userData, setUserData] = React.useState(user);
  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = () => {
    dispatch(updateProfile(userData, history))
  }

  return (
    <div>
        <form onSubmit={handleSubmit} method="post">
            <Typography>Public</Typography>
            <Paper elevation={3}>
                <BasicProfile userData={userData} setUserData={setUserData}/>
                <hr></hr>
                <CheckBoxInfo userData={userData} setUserData={setUserData}/>
                <hr></hr>
                <MultipleSelect userData={userData} setUserData={setUserData}/>
            </Paper>
            <Typography>Private</Typography>
            <Paper elevation={3}>
                <RadioComp userData={userData} setUserData={setUserData}/>
                <hr></hr>
                <Address userData={userData} setUserData={setUserData}/>
            </Paper>
            <Button variant="contained" type="submit" color="primary">Update</Button>
        </form>
    </div>
  );
}

export default Profile;
